package waltid.openbadgecredential.cli

import picocli.CommandLine
import picocli.CommandLine.Command
import picocli.CommandLine.Model.CommandSpec
import picocli.CommandLine.Spec
import java.io.File
import kotlin.system.exitProcess


@Command(
        name = "verify",
        mixinStandardHelpOptions = true,
        description = ["Presents a verifiable credential for verification"]
)
class VerifyCmd : Runnable {

    @CommandLine.Option(names = [ "-f", "--file" ],
            defaultValue = "jwt.json",
            description =   ["File with a signed JWT-encoded credential to be presented. Defaults to 'jwt.json'"])
    lateinit var fileSource : File

    @CommandLine.Option(names = ["-i", "--inline" ],
            required = false,
            description = ["The signed JWT-encoded credential to be presented. Takes precedence over -f option."])
    lateinit var inlineSource: String;

    @Spec
    var spec: CommandSpec? = null

    override fun run() {

        var jws : String

        if (this::inlineSource.isInitialized && inlineSource != null) {
            println("Getting JWT from the command line argument.")
            jws = inlineSource
        } else {

            if (fileSource.exists()) {
                println("Getting JWT from $fileSource file.")
                jws = fileSource.readText()
            } else {
                println("No option provided. Default to '-f $fileSource', but file doesn't exist. It seems you haven't created a presentation yet. Check `walt present` command")
                spec?.parent()?.commandLine()?.usage(System.err);

                exitProcess(-1)
            }

        }

        println("")
        println("+-------------+")
        println("| Encoded JWS |")
        println("+-------------+")
        println(jws)
        println()

        // EdDSA
        // val validJWSWithEdDSA = "eyJhbGciOiJFZERTQSIsInR5cCI6IkpXVCIsImtpZCI6ImRpZDprZXk6ejZNa2hhYWRaUndyb0FORFZ1ZWRoOVhyTGZKQk5KVkhyTkRZVEZrNmJZSGRLTEVoIn0..4oRvm68fsIUtzGtvweuJnUtQeEV_ZoCt-Nvb_SIYl-vnjmif3nWzq9aqRERfUFBXtOzptApXCXlP0bFXl_HtDw"
        // val tamperedJWSWithEdDSA = "eyJhbGciOiJFZERTQSIsInR5cCI6IkpXVCIsImtpZCI6ImRpZDprZXk6ejZNa2hhYWRaUndyb0FORFZ1ZWRoOVhyTGZKQk5KVkhyTkRZVEZrNmJZSGRLTEVoIn0..4oRvm68fsIUtzGtvweuJnUtQeEV_ZoCt-Nvb_SIYl-vnjmif3nWzq9aqRERfUFBXtOzptApXCXlP1bFXl_HtDw"

        // RSA
        // Unsupported JWS algorithm RS256, must be HS256, HS384 or HS512
        // val validJWSWithRSA = "..ouiBNOqWyHKLeW4IyJIrmfAlBoAw6TLIUVfsVjcMjJgqu_nrC_roxEvTf-qKlBTFeD_QhVNx4CYidcGuEPme7R2qi4GYDUqVhykD-snpHFTyyrm8t6cDY43-Y0uQRGfOJlGfePMOQbEGf5x3yj71Y2YTdvgVo1JouY7qNqFDwb63x1FjGY4QtVBWs-iUDkc7zZjxS0uY0GZ2v3I3XTMJdqJ711Dk-BDcytaW7urFlbXa2IdV-8DfNftk7TNbQSAxgr4ebxfcwrAtkY6i5cjYaHnFtZuqztZBVWV3W7j-OsxsLIMuFBllQ70YMoOS6JdXSoQKf5-Cbgtvr4jTGQvkkA"

        // secp256r1
        // Unsupported JWS algorithm ES256, must be HS256, HS384 or HS512
        // val validJWSWithSecp256r1 = "eyJhbGciOiJFUzI1NiIsInR5cCI6IkpXVCIsImtpZCI6ImRpZDprZXk6eldtdTlSQ3JTM2hCaEdTeWhLZk5TeXd1VFdGQ01qTXhKZUtoTVB0NmtDaFMxdDNmdFdEcVdad3VWN3FRajY2dlJGVHFYWGRHaG9GZkVMYTRtWmFGRFNqZDUxb3VUTFhOcWdoVDhSZThGNVFMV2p4Rm9YOWNNckc5eHVVaFk0eGYifQ..iebkH9UaD99C7Eoc4XXfoNmIveljYwsnMkRqfKu_c2JUuJNd07tEE_zBuOyAvPaqZzzitWJ_YH9paFXUokqdLQ"

